#include <iostream>

using namespace std;


long digit_sum(char digit[], int from){

  long sum = 1;
  for(int t = 0; t < 13; t++){
    sum *= digit[from+t] - '0';
  }

  return sum;
}



int main(int argc, char* argv[]){

  char digit[] = "";

  long largest = 0;

  for(int n = 0; n < sizeof(digit) - 13; n++){
    long res = digit_sum(digit, n);
    if(res > largest) largest = res;
  }

  cout << "euler solution 8: " << largest << endl;

  return 0;
}
